          <Box className={classes.EventIconCardWrapper}>
            {EventIconCardsData.map((data) => (
              <EventIconCard key={data.id} data={data} />
            ))}
          </Box>
        </Container>
      </section>
      {/* Products Section */}
      {adslist && adslist.homepage2 && (
        <Advertisement
          adImg={adslist.homepage2.link}
          adlink={adslist.homepage2.openlink}
