                {sellType ? sellType : 'Category'} <ExpandMoreRoundedIcon />
              </Button>
              <Popper
                open={opensell}
                anchorEl={anchorRefSell.current}
                role={undefined}
                transition
                disablePortal
              >
                {({ TransitionProps, placement }) => (
                  <Grow
                    {...TransitionProps}
                    style={{
