                  >
                    <Paper>
                      <ClickAwayListener onClickAway={handleCloseSell}>
                        <MenuList
                          autoFocusItem={open}
                          id="menu-list-grow"
                          onKeyDown={handleListKeyDown}
                        >
                          {IconCardsData.map((data, index) => (
                            <MenuItem onClick={(e) => handleCloseSell(e, data)}>
                              {data.title}
                            </MenuItem>
